package study.ex03;

public class Person {
	private String name;
	private String phone;
	
	public Person(String name) {
		// TODO Auto-generated constructor stub
		this.name=name;
	}
	
	public void setPhone(String phone) {
		this.phone = phone;
	}
	
	public String getName() {
		return name;
	}
	
	public String getPhone() {
		return phone;
	}
}